CREATE PROCEDURE [dbo].[pa_InsertUpdateGesArticuloClaseConEscandallo]
	@CodClaseHijo int,
	@CodClasePadre int,
	@Codigo int,
	@Cantidad decimal(18, 4),
	@CodMagnitud int
AS

SET NOCOUNT ON

IF EXISTS(SELECT [Codigo] FROM [dbo].[GesArticuloClaseConEscandallo] WHERE [Codigo] = @Codigo)
BEGIN
	UPDATE [dbo].[GesArticuloClaseConEscandallo] SET
		[CodClaseHijo] = @CodClaseHijo,
		[CodClasePadre] = @CodClasePadre,
		[Cantidad] = @Cantidad,
		[CodMagnitud] = @CodMagnitud
	WHERE
		[Codigo] = @Codigo
END
ELSE
BEGIN
	INSERT INTO [dbo].[GesArticuloClaseConEscandallo] (
		[CodClaseHijo],
		[CodClasePadre],
		[Codigo],
		[Cantidad],
		[CodMagnitud]
	) VALUES (
		@CodClaseHijo,
		@CodClasePadre,
		@Codigo,
		@Cantidad,
		@CodMagnitud
	)
END

