CREATE PROCEDURE [dbo].[pa_InsertUpdategesGrupo]
	@CodGrupo int,
	@Descripcion varchar(100),
	@Activo bit
AS

SET NOCOUNT ON

IF EXISTS(SELECT [CodGrupo] FROM [dbo].[gesGrupo] WHERE [CodGrupo] = @CodGrupo)
BEGIN
	UPDATE [dbo].[gesGrupo] SET
		[Descripcion] = @Descripcion,
		[Activo] = @Activo
	WHERE
		[CodGrupo] = @CodGrupo
END
ELSE
BEGIN
	INSERT INTO [dbo].[gesGrupo] (
		[CodGrupo],
		[Descripcion],
		[Activo]
	) VALUES (
		@CodGrupo,
		@Descripcion,
		@Activo
	)
END

--endregion


