CREATE PROCEDURE [dbo].[pa_InsertUpdategesProducto_Num]
	@CodProducto uniqueidentifier,
	@CodGrupo int,
	@CodFamilia int,
	@CodZona int,
	@CodProducto_Num int,
	@Descripcion varchar(100),
	@Activo bit,
	@Precio decimal(18, 2),
	@ConEscandallo bit
AS

SET NOCOUNT ON

IF EXISTS(SELECT [CodProducto_Num] FROM [dbo].[gesProducto_Num] WHERE [CodProducto_Num] = @CodProducto_Num)
BEGIN
	UPDATE [dbo].[gesProducto_Num] SET
