	WHERE
		[CodProducto_Num] = @CodProducto_Num
END
ELSE
BEGIN
	INSERT INTO [dbo].[gesProducto_Num] (
		[CodProducto],
		[CodGrupo],
		[CodFamilia],
		[CodZona],
		[CodProducto_Num],
		[Descripcion],
		[Activo],
		[Precio],
		[ConEscandallo]
	) VALUES (
		@CodProducto,
		@CodGrupo,
		@CodFamilia,
		@CodZona,
		@CodProducto_Num,
		@Descripcion,
		@Activo,
		@Precio,
