CREATE PROCEDURE [dbo].[pa_SelectgesArqueoDetallesByCodArqueo]
	@CodArqueo uniqueidentifier
AS

SET NOCOUNT ON
SET TRANSACTION ISOLATION LEVEL READ COMMITTED

SELECT
	[CodDetalle],
	[CodArqueo],
	[Realidad],
	[Contado],
	[Registro],
	[Vendido],
	[Diferencia],
	[Periodo]
FROM
	[dbo].[gesArqueoDetalle]
WHERE
	[CodArqueo] = @CodArqueo

