CREATE PROCEDURE [dbo].[pa_SelectgesCliente]
	@CodCliente int
AS

SET NOCOUNT ON
SET TRANSACTION ISOLATION LEVEL READ COMMITTED

SELECT
	[CodCliente],
	[Nombre],
	[Apellidos],
	[Empresa],
	[Activo],
	[CIF],
	[Direccion],
	[Localidad],
	[Provincia],
	[CP],
	[CodClase],
	[Telefono1]
FROM
	[dbo].[gesCliente]
WHERE
	[CodCliente] = @CodCliente

