CREATE PROCEDURE [dbo].[pa_SelectgesEscandallosByCodMagnitud]
	@CodMagnitud int
AS

SET NOCOUNT ON
SET TRANSACTION ISOLATION LEVEL READ COMMITTED

SELECT
	[CodEscandallo],
	[CodProducto],
	[CodClase],
	[Clase],
	[Cantidad],
	[CodMagnitud]
FROM
	[dbo].[gesEscandallo]
WHERE
	[CodMagnitud] = @CodMagnitud

