CREATE PROCEDURE [dbo].[pa_SelectgesPedidosByCodTrabajador]
	@CodTrabajador int
AS

SET NOCOUNT ON
SET TRANSACTION ISOLATION LEVEL READ COMMITTED

SELECT
	[CodPedido],
	[CodProveedor],
	[CodTrabajador],
	[Pedido],
	[Recibido],
	[Facturado],
	[Pagado],
	[Telefono],
	[Orden],
	[Albaran],
	[Factura],
	[Forma_pago],
	[Total_pedido],
	[Total_recibido],
	[Total_pagado],
	[Total],
	[CodFormaPago],
	[Observacion],
	[FechaCreacion],
	[CodTipoPedido]
FROM
	[dbo].[gesPedido]
WHERE
	[CodTrabajador] = @CodTrabajador

