SELECT
	[CodPedido],
	[CodProveedor],
	[CodTrabajador],
	[Pedido],
	[Recibido],
	[Facturado],
	[Pagado],
	[Telefono],
	[Orden],
	[Albaran],
	[Factura],
	[Forma_pago],
	[Total_pedido],
	[Total_recibido],
	[Total_pagado],
	[Total],
	[CodFormaPago],
	[Observacion],
	[FechaCreacion]
FROM
	[dbo].[gesPedido]
WHERE
	[Factura] = @Factura

