CREATE PROCEDURE [dbo].[pa_SelectgesProducto_NumsDynamic]
	@WhereCondition nvarchar(500),
	@OrderByExpression nvarchar(250) = NULL
AS

SET NOCOUNT ON
SET TRANSACTION ISOLATION LEVEL READ COMMITTED

DECLARE @SQL nvarchar(3250)

SET @SQL = '
SELECT
	[CodProducto],
	[CodGrupo],
	[CodFamilia],
	[CodZona],
	[CodProducto_Num],
	[Descripcion],
	[Activo],
	[Precio],
	[ConEscandallo]
FROM
	[dbo].[gesProducto_Num]
WHERE
	' + @WhereCondition

IF @OrderByExpression IS NOT NULL AND LEN(@OrderByExpression) > 0
BEGIN
	SET @SQL = @SQL + '
ORDER BY
	' + @OrderByExpression
END

EXEC sp_executesql @SQL

--endregion


