CREATE PROCEDURE [dbo].[pa_SelectgesProveedore]
	@CodProveedor int
AS

SET NOCOUNT ON
SET TRANSACTION ISOLATION LEVEL READ COMMITTED

SELECT
	[CodProveedor],
	[Nombre],
	[Apellidos],
	[Empresa],
	[TelefonoFijo],
	[TelefonoMovil],
	[Cif],
	[Direccion],
	[Localidad],
	[Provincia],
	[Activo],
	[Email],
	[TipoPago],
	[NRSanitario]
FROM
	[dbo].[gesProveedores]
WHERE
	[CodProveedor] = @CodProveedor

